package com.trello.clone.domain.model.board.values;

import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class ValidatorTest {

  @Test
  void validateMinLength() {
    assertThrows(IllegalArgumentException.class, () -> Validator.validateMinLength(""));
  }

  @Test
  void validateFormat() {
    assertThrows(IllegalArgumentException.class, () -> Validator.validateFormat("!@#$%"));
  }

  @Test
  void validateMaxLength() {
    assertThrows(IllegalArgumentException.class, () -> Validator.validateMaxLength(""));
  }

  @Test
  void validateIfIsEmpty() {
    assertThrows(IllegalArgumentException.class, () -> Validator.validateIfIsEmpty(""));
  }

  @Test
  void validateIfIsNull() {
    assertThrows(IllegalArgumentException.class, () -> Validator.validateIfIsEmpty(null));
  }
}